import React, { useState } from "react";
import "./css-files/Signup.css";
import { Link /*, redirect */ } from "react-router-dom";
/*import {
  Box,
  Avatar,
  TextField,
  Button,
  Typography,
  Link as Nv,
} from "@material-ui/core";*/
import axios from "axios";

function SignUp() {
  const BASE_URL = "http://localhost:3007";
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [mobile, setMobile] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showOTPField, setShowOTPField] = useState(false);
  const [otpResponse, setOtpResponse] = useState(false);
  // const counter = React.useRef(59);
  const [resend,setResend] = useState(1);
//test
 const [count,setCount] = useState(1);
//test
const [otpcount,setotpCount] = useState(120);

  const [otp, setOTP] = useState("");
  // /const [otpemail, setOTPemail] = useState("");/ 

  const handleSubmit = (event) => {
    event.preventDefault();

    if (mobile && mobile.length === 10) {
      if (password.length >= 6) {
        if (password === confirmPassword) {
          //verifyemail
          axios({
            method: "post",
            url: `${BASE_URL}/verifydata`,
            data: {
              email: email,
              //mobileNumber: mobile,
            },
          }).then(
            (response) => {
              initiateOTP();
            },
            (erro) => {
              alert(erro?.response?.data?.mesg);
            }
          );
         
        } else {
          alert("Password did not match");
        }
      } else {
        alert("Password Must be atleast 6 characters");
      }
    } else {
      alert("Invalid Mobile No");
    }
  };



 /* const [counter, setCounter] = React.useState(59);
  React.useEffect(() => {
    const timer = setInterval(() => {
      setCounter(prevCount => prevCount - 1); // <-- Change this line!
      if(counter === 0) {
        clearInterval(timer);
      }
    }, 1000);
    return () => {
      clearInterval(timer);
    };
  }, []);*/

 const otptimer=(event)=>{
  const i=120
  for(i=120;i<0;i--){
    setotpCount(119)
  }
  if(i===0){
    alert("OTP EXPIRED")
  }
 }
  
  const initiateOTP = (event) => {
    // event.preventDefault();
    axios({
      method: "post",
      url: `${BASE_URL}/sendotp`,
      data: {
        mobileNumber: mobile,
      },
    }).then((response) => {
      setShowOTPField(true);
     // setCounter(59)
      // counter.current = 59
      otptimer();
      setOtpResponse(response?.data);
    });
  };

  const initiateCreateUser = () => {
    axios({
      method: "post",
      url: `${BASE_URL}/createUser`,
      data: {
        mobileNumber: mobile,
        name: name,
        email: email,
        password: password,
      },
    }).then((response) => {
      // setShowOTPField(true)
       alert(`Verified OTP Successfully and User ${name} Created `);
       window.location.replace('/Login') 
       //setOtpResponse(response)
     });
   };





  const handleVerifyOTP = (event) => {
    event.preventDefault();
    axios({
      method: "post",
      url: `${BASE_URL}/verifyotp`,
      data: {
        otp: otp,
        id: otpResponse?.id,
      },
    }).then(
      (response) => {
        initiateCreateUser();
      },
      (erro) => {
       /* if(erro?.response?.data?.mesg === "OTP expired"){
          alert("The old OTP is expired new otp will be sent now")
          setCount(1)
          initiateOTP()
        }else{
         // alert("I am here")
          alert(erro?.response?.data?.mesg)
        setCount(count+1)
        if(count===3){
          alert("User Creation failed Due to max otp fails, try agin")
          window.location.replace("/Signup")
        }
      }*/
      if(erro?.response?.data?.mesg === "OTP expired"){
        setResend(resend+1)
        
        if(resend===3){
          alert("Resend OTP limit reached, Try again later")
          window.location.replace("/Signup")
        }else{
          alert("The old OTP is expired new otp will be sent now")
          setCount(1)
          initiateOTP()
        }
      
      }else{
        alert(erro?.response?.data?.mesg)
      setCount(count+1)
      if(count===3){
        alert("User Creation failed Due to max otp fails, try agin")
        window.location.replace("/Signup")
      }
    }
      }
    );
    //console.log(`OTP: ${otp} verified`);
  };
  // const getCounter =()=>{
  //   return counter.current
  // }

  return (
    <div id="signup-body">
      <div className="signup-container">
        {!showOTPField && (
          <form onSubmit={handleSubmit} className="form-signup">
            <h2 id="heading">Sign Up</h2>
            <div className="form-groupsignup">
              <input
                className="input-sigup"
                type="text"
                id="name-signup"
                value={name}
                placeholder="Name"
                onChange={(event) => setName(event.target.value)}
                required
              />
            </div>
            <div className="form-groupsignup">
              <input
                className="input-sigup"
                type="email"
                id="email-signup"
                value={email}
                placeholder="Email id"
                onChange={(event) => setEmail(event.target.value)}
                required
              />
            </div>

            <div className="form-groupsignup">
              <input
                className="input-sigup"
                type="tel"
                id="mobile-signup"
                value={mobile}
                placeholder="Mobile no"
                onChange={(event) => setMobile(event.target.value)}
                required
              />
              {/* <button type="button" className="btn-verify" onClick={initiateOTP}>
            Send
          </button> */}
            </div>

            <div className="form-groupsignup">
              <input
                className="input-sigup"
                type="password"
                id="password-signup"
                value={password}
                placeholder="Password"
                onChange={(event) => setPassword(event.target.value)}
                required
              />
            </div>
            <div className="form-groupsignup">
              <input
                className="input-sigup"
                type="password"
                id="confirmPassword-signup"
                value={confirmPassword}
                placeholder="Confirm Password"
                onChange={(event) => setConfirmPassword(event.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn-signup">
              Sign Up
            </button>
            <p id="loginlink-container">
              Already Registred ?{" "}
              <Link to="/Login" id="login-link">
                Login
              </Link>
            </p>
          </form>
        )}
        {showOTPField && (
          <form onSubmit={handleVerifyOTP} className="form-signup">
            <div className="form-groupsignup">
              <input
                className="input-sigup"
                type="text"
                id="otp-signup"
                value={otp}
                placeholder="OTP"
                onChange={(event) => setOTP(event.target.value)}
                required
              />
             {/* <Box mt={3}>
                <Typography fontWeight={400} align="center" color="white">
                  {" "}
                  Resend OTP in{" "}
                  <span style={{ color: "green", fontWeight: "bold" }}>
                    {" "}
                    00:{counter}
                  </span>{" "}
                </Typography>
        </Box>*/}
            </div>
            <button type="submit" className="btn-signup">
              Verify OTP
            </button>
            <br />
            <div>
              <p>OTP Expries in {otpcount}</p>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

export default SignUp;
