import React, { /*useRef, */useState, useEffect } from 'react';
import './contact.css';
import emailjs from "@emailjs/browser";

function Contact() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message,setMessage] = useState("");

 //const form = useRef();

  const sendEmail = (event) => {
    event.preventDefault();

   emailjs.sendForm(
       'service_oqq0osq', 'template_mbrxdan', event.target, 'xTN8RKQjVHyzK0wxG'
      )
      .then(
        (result) => {
          console.log(result.text);
          alert("Your Message has been Sent");
          window.location.replace("/");
        },
        (error) => {
          console.log(error.text);
        }
      );
    event.target.reset();

  };
  const callContactpage = async () => {
    try {
      const res = await fetch("/BookingPage", {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        credentials: "include",
      });
      const data = await res.json();
      console.log(data);

      if (!res.status === 200) {
        const error = new Error(res.error);
        throw error;
      }
    } catch (err) {
      console.log(err);
      window.location.replace("/login");
      alert("Please login to use this feature");
    }
  };
  useEffect(() => {
    callContactpage();
  }, []);

  return (
      <div id="contact-body">
        <div id="contact-container">
        <form id="contact-form" onSubmit={sendEmail}>
          <h2 id="contact-heading">Contact Us</h2>
          <div className="form-group-contact">
          <input 
           className="input-contact"
          type="text" 
          id="name-contact"
          placeholder='Name'
          value={name}
          name="user_contact_name"
          onChange={(event) => setName(event.target.value)}
          required
          
          />
          </div>
          <div className="form-group-contact">
          <input
              className="input-contact"
              type="email"
              id="email-contact"
              value={email}
              placeholder="Email id"
              name="user_contact_email"
              onChange={(event) => setEmail(event.target.value)}
              required
            />
          </div>
          <div className="form-group-contact">
          <textarea className="input-contact" id="contct-message" name ="user_contact_message" value={message} placeholder='Your Message' onChange={(event) => setMessage(event.target.value)}></textarea>
          </div>
          <button type="submit" className="btn-contact">
            Send
          </button>
          </form>
        </div>
      </div>
  )
}

export default Contact;
